//Teachers
class Teacher {
    constructor(firstName, lastName, age, id, avarage, img, numClass) {
        this.firstName = firstName;
        this.lastName = lastName;
        this.age = age;
        this.id = id;
        this.avarage = avarage;
        this.img = img;
        this.numClass = numClass;
    }

    calcAvarage(arr)
    {
        let calc = 0;
        let counter = 0;
        for(let d = 0; d<arr.length; d++)
        {
            if(arrStudents[d].numClass == this.numClass)
            {
                calc+=arrStudents[d].grade;
                counter++;
            }
        }

        let avr = calc/counter;
        return avr;
    }
}

